package javaintern;
import java.util.*;
public class nEven 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		System.out.println("Enter a Number TO Print Even Number ");
		Scanner sc=new Scanner(System.in);
		int n=sc.nextInt();
		for(int i=0; i<n; i++)
		{
			if((i%2==0))
			{
					System.out.println(i);
			
			}
		
		
			
		}

	}
}

	
